--Welcome message on home page 
--This is within a static element and this PL/SQL function is in a READONLY display

DECLARE
   cur_name VARCHAR (255);
   welc_msg VARCHAR (255);
   default_msg VARCHAR (255);
BEGIN
   SELECT name
   INTO cur_name
   from F15D4_Employee
   where employee_id = :CURRENT_EMPLOYEE;
   
   welc_msg := 'Hi ' || cur_name || '!';
   RETURN welc_msg;
END;


--- Admin Panel Creation 
--- Has a chart with the following query

--- Series One
select null link, RFE_ID label, COUNT(RFE_ID) value1
from  "XMR73"."F15D4_RFE"
 group by RFE_ID


--- After the chart there is a List of Values which are links
--- to other portions
--- The links were to:
--- Create lab
--- Create Employee



--- HIDE PENDING RFE Page from Lab Employees
--- Really this is our page that we will actually allow
--- approvers to EDIT a RFE
DECLARE
    emp_type VARCHAR(255);
BEGIN
SELECT type
INTO emp_type
from F15D4_Employee
where employee_id = :CURRENT_employee;

IF emp_type = 'Lab Employee' THEN
     RETURN FALSE;
ELSE 
     RETURN TRUE;
END IF;
END;



